import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import TopHeader from './Main';

function CountButton() {
  const [count, setCount] = useState(0);
  const incrementBy1 = () => setCount(count + 1);

  return (
    <div>
      <button onClick={incrementBy1}>Add 1</button>
      <br></br>
      <h4>{count}</h4>
    </div>
  );
}

function Counter(props: any) {
  return (
    <div>
      <button onClick={props.counting}>Add 1</button>
      <br></br>
    </div>
  );
}

function Amount(props: any) {
  return <h4>Count: {props.num}</h4>;
}

class Welcome extends React.Component {
  render() {
    return (
      <div className="row">
        <div className="text-center">
          <h2>Home</h2>
        </div>
        <div className="text-center">
          <img src="./Joel.jpg" alt="Joel the great" />
        </div>
    </div>
    );
  }
}

class Conclusion extends React.Component {
  render() {
    return <h2>Give some a look</h2>;
  }
}

function App() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <Welcome />
      <TopHeader />
      <br></br>
      <CountButton />
      <Counter counting={() => setCount(count + 1)} />
      <Amount num={count} />
      <Conclusion />
    </div>
  );
}

export default App;
